(()=>{"use strict";new class extends class{constructor(){this.todos=[]}addTodo(t,e){return this.todos.push({name:t,completed:e,completed:!1})}addDescription(t,e){return this.todos[t].description=e}addPriority(t,e){return this.todos[t].priority=e}addDate(t,e){return this.todos[t].dueDate=e}}{constructor(t){super(t)}deleteTodo(t){return this.todos.splice(t,1)}toggleTodo(t){const e=this.todos[t];return e.completed=!e.completed}editName(t,e){return this.todos[t].name=e}editDescription(t,e){return this.todos[t].description=e}editPriority(t,e){return this.todos[t].priority=e}editDate(t,e){return this.todos[t].dueDate=e}};const t=document.querySelector(".openNav");document.querySelector(".navbar");const e=new class{constructor(){this.sideNav=document.querySelector(".openNav"),this.navbar=document.querySelector(".navbar")}openNav(){this.sideNav.classList.toggle("removed"),this.navbar.classList.toggle("removed")}};t.addEventListener("click",(()=>{e.openNav()}))})();